import React, { useState } from 'react';
import "./atomichabits.css"
import Ahc1 from './ahc1/ahc1';

const Bookinfo = () => {


        const [items, setItems] = useState([
        { id: 1, title: 'Chapter 1', content:  <Ahc1/>},
        { id: 2, title: 'Chapter 2', content: 'Content for Item 2' },
        { id: 3, title: 'Chapter 3', content: 'Content for Item 3' },
        ]);

        const toggleItem = (id) => {
        setItems((prevItems) =>
        prevItems.map((item) =>
        item.id === id ? { ...item, isOpen: !item.isOpen } : item
        )
        );
        };

        return (
        <div className='bookinfo'>
        <h1 className='title'>Atomic habits</h1>
        <ul>
        {items.map((item) => (
                <li key={item.id}>
                <div className='chapter'>
                {item.isOpen ? <i className="fa fa-caret-down"></i> : <i className="fa fa-caret-right"></i>}
                <h3 className="togglelist" onClick={() => toggleItem(item.id)}>
                {item.title} 
                </h3>
                </div>
                {item.isOpen && <p>{item.content}</p>}
                </li>
        ))}
        </ul>
        </div>
  );
};

export default Bookinfo;
