import React, { useState } from 'react';
import "./atomichabits.css"
import Ahc1 from './Chapters/ahc1';
import Ahc2 from './Chapters/ahc2';
import Ahc3 from './Chapters/ahc3'

const Atomichabits = () => {


        const [items, setItems] = useState([
        { id: 1, title: 'Chapter 1', content:  <Ahc1/>},
        { id: 2, title: 'Chapter 2', content:  <Ahc2/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc3/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc3/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc4/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc5/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc6/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc7/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc8/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc9/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc10/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc11/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc12/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc13/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc14/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc15/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc16/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc17/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc18/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc19/> },
        { id: 3, title: 'Chapter 3', content:  <Ahc20/> },
        ]);

        const toggleItem = (id) => {
        setItems((prevItems) =>
        prevItems.map((item) =>
        item.id === id ? { ...item, isOpen: !item.isOpen } : item
        )
        );
        };

        return (
        <div className='bookinfo'>
        <h1 className='title'>Atomic habits</h1>
        <ul>
        {items.map((item) => (
                <li key={item.id}>
                <div className='chapter'>
                {item.isOpen ? <i className="fa fa-caret-down"></i> : <i className="fa fa-caret-right"></i>}
                <h2 className="togglelist" onClick={() => toggleItem(item.id)}>
                {item.title} 
                </h2>
                </div>
                {item.isOpen && <p>{item.content}</p>}
                </li>
        ))}
        </ul>
        </div>
  );
};

export default Atomichabits;
